import React, { useState, useEffect } from 'react';
import { translations } from '../utils/translations';

const PortEVLanding = () => {
  const [language, setLanguage] = useState('en');
  const [targetAudience, setTargetAudience] = useState('residential');
  const [scrolled, setScrolled] = useState(false);

  const t = translations[language];

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleLanguage = () => {
    setLanguage(prev => prev === 'en' ? 'pt' : 'en');
  };

  const scrollToSection = (sectionId) => {
    document.getElementById(sectionId)?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div style={{ fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif", lineHeight: '1.6', color: '#1f2937' }}>
      {/* Navigation */}
      <nav style={{
        position: 'fixed',
        top: 0,
        width: '100%',
        background: scrolled ? 'rgba(255, 255, 255, 0.98)' : 'rgba(255, 255, 255, 0.95)',
        backdropFilter: 'blur(10px)',
        borderBottom: '1px solid rgba(37, 99, 235, 0.1)',
        padding: '1rem 0',
        zIndex: 1000,
        transition: 'all 0.3s ease',
        boxShadow: scrolled ? '0 4px 20px rgba(0, 0, 0, 0.1)' : 'none'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 2rem',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
            <div style={{
              width: '50px',
              height: '50px',
              background: 'linear-gradient(135deg, #2563eb, #3b82f6)',
              borderRadius: '12px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontWeight: 'bold',
              fontSize: '1.2rem'
            }}>
              [LOGO]
            </div>
            <div style={{
              fontSize: '1.8rem',
              fontWeight: '700',
              background: 'linear-gradient(135deg, #2563eb, #3b82f6)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              backgroundClip: 'text'
            }}>
              PortEV
            </div>
          </div>

          <div style={{ display: 'flex', listStyle: 'none', gap: '2rem', alignItems: 'center' }}>
            <a onClick={() => scrollToSection('home')} style={{ 
              textDecoration: 'none', 
              color: '#1f2937', 
              fontWeight: '500', 
              cursor: 'pointer',
              position: 'relative'
            }}>{t.nav.home}</a>
            <a onClick={() => scrollToSection('problems')} style={{ 
              textDecoration: 'none', 
              color: '#1f2937', 
              fontWeight: '500', 
              cursor: 'pointer' 
            }}>{t.nav.problems}</a>
            <a onClick={() => scrollToSection('product')} style={{ 
              textDecoration: 'none', 
              color: '#1f2937', 
              fontWeight: '500', 
              cursor: 'pointer' 
            }}>{t.nav.product}</a>
            <a onClick={() => scrollToSection('features')} style={{ 
              textDecoration: 'none', 
              color: '#1f2937', 
              fontWeight: '500', 
              cursor: 'pointer' 
            }}>{t.nav.features}</a>
            <a onClick={() => scrollToSection('advantages')} style={{ 
              textDecoration: 'none', 
              color: '#1f2937', 
              fontWeight: '500', 
              cursor: 'pointer' 
            }}>{t.nav.advantages}</a>
            <a onClick={() => scrollToSection('pricing')} style={{ 
              textDecoration: 'none', 
              color: '#1f2937', 
              fontWeight: '500', 
              cursor: 'pointer' 
            }}>{t.nav.pricing}</a>
          </div>

          <button onClick={toggleLanguage} style={{
            background: 'linear-gradient(135deg, #2563eb, #3b82f6)',
            color: 'white',
            border: 'none',
            padding: '0.5rem 1rem',
            borderRadius: '25px',
            cursor: 'pointer',
            fontWeight: '500',
            transition: 'all 0.3s ease'
          }}>
            {language === 'en' ? 'PT' : 'EN'}
          </button>
        </div>
      </nav>

      {/* Hero Section */}
      <section id="home" style={{
        background: 'linear-gradient(135deg, #2563eb, #3b82f6)',
        color: 'white',
        padding: '120px 0 80px',
        textAlign: 'center',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundImage: 'url("https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&auto=format&fit=crop&w=2000&q=80")',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          opacity: 0.1,
          zIndex: 0
        }}></div>

        <div style={{
          position: 'relative',
          zIndex: 1,
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 2rem'
        }}>
          <h1 style={{
            fontSize: 'clamp(2.5rem, 5vw, 4rem)',
            fontWeight: '700',
            marginBottom: '1.5rem',
            lineHeight: '1.2'
          }}>
            {t.hero.title}
          </h1>
          <p style={{
            fontSize: 'clamp(1.1rem, 2vw, 1.5rem)',
            marginBottom: '2rem',
            opacity: '0.9',
            maxWidth: '600px',
            marginLeft: 'auto',
            marginRight: 'auto'
          }}>
            {t.hero.subtitle}
          </p>

          <div style={{
            width: '100%',
            maxWidth: '800px',
            height: '400px',
            background: 'rgba(255, 255, 255, 0.1)',
            borderRadius: '20px',
            margin: '3rem auto 0',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '1.2rem',
            border: '2px dashed rgba(255, 255, 255, 0.3)'
          }}>
            {t.hero.imageAlt}
          </div>
        </div>
      </section>

      {/* Problems Section */}
      <section id="problems" style={{ padding: '80px 0', backgroundColor: '#f8fafc' }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 2rem' }}>
          <h2 style={{
            fontSize: 'clamp(2rem, 4vw, 3rem)',
            fontWeight: '700',
            textAlign: 'center',
            marginBottom: '1rem',
            background: 'linear-gradient(135deg, #2563eb, #3b82f6)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            {t.problems.title}
          </h2>
          <p style={{
            fontSize: '1.2rem',
            textAlign: 'center',
            color: '#6b7280',
            marginBottom: '4rem',
            maxWidth: '600px',
            marginLeft: 'auto',
            marginRight: 'auto'
          }}>
            {t.problems.subtitle}
          </p>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
            gap: '2rem',
            marginBottom: '3rem'
          }}>
            {Object.entries(t.problems).slice(2).map(([key, problem], index) => (
              <div key={key} style={{
                background: 'white',
                padding: '2rem',
                borderRadius: '15px',
                boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
                transition: 'all 0.3s ease',
                border: '1px solid rgba(37, 99, 235, 0.1)'
              }}>
                <div style={{
                  width: '60px',
                  height: '60px',
                  background: 'linear-gradient(135deg, #ef4444, #dc2626)',
                  borderRadius: '15px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  marginBottom: '1.5rem',
                  fontSize: '1.5rem'
                }}>
                  âš ï¸
                </div>
                <h3 style={{
                  fontSize: '1.3rem',
                  fontWeight: '600',
                  marginBottom: '1rem',
                  color: '#1f2937'
                }}>
                  {problem.title}
                </h3>
                <p style={{
                  color: '#6b7280',
                  lineHeight: '1.6'
                }}>
                  {problem.description}
                </p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Product Section */}
      <section id="product" style={{ padding: '80px 0', backgroundColor: 'white' }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 2rem' }}>
          <h2 style={{
            fontSize: 'clamp(2rem, 4vw, 3rem)',
            fontWeight: '700',
            textAlign: 'center',
            marginBottom: '1rem',
            background: 'linear-gradient(135deg, #2563eb, #3b82f6)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            {t.product.title}
          </h2>
          <p style={{
            fontSize: '1.2rem',
            textAlign: 'center',
            color: '#6b7280',
            marginBottom: '2rem',
            maxWidth: '700px',
            marginLeft: 'auto',
            marginRight: 'auto'
          }}>
            {t.product.subtitle}
          </p>

          <div style={{
            background: 'linear-gradient(135deg, rgba(37, 99, 235, 0.05), rgba(59, 130, 246, 0.05))',
            padding: '3rem',
            borderRadius: '20px',
            marginBottom: '4rem',
            border: '1px solid rgba(37, 99, 235, 0.1)'
          }}>
            <p style={{
              fontSize: '1.1rem',
              lineHeight: '1.8',
              color: '#1f2937',
              textAlign: 'center',
              maxWidth: '800px',
              margin: '0 auto'
            }}>
              {t.product.description}
            </p>
          </div>

          <div style={{
            width: '100%',
            height: '300px',
            background: 'rgba(37, 99, 235, 0.1)',
            borderRadius: '15px',
            margin: '3rem auto',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '1.1rem',
            border: '2px dashed rgba(37, 99, 235, 0.3)',
            color: '#2563eb'
          }}>
            Product Image Placeholder
          </div>

          <h3 style={{
            fontSize: '2rem',
            fontWeight: '600',
            textAlign: 'center',
            marginBottom: '3rem',
            color: '#1f2937'
          }}>
            {t.product.components.title}
          </h3>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
            gap: '2rem'
          }}>
            {Object.entries(t.product.components).slice(1).map(([key, component], index) => {
              const icons = ['ðŸ”‹', 'ðŸ§ ', 'ðŸ¤–', 'âš¡', 'ðŸ“±', 'ðŸ”„'];
              return (
                <div key={key} style={{
                  background: 'white',
                  padding: '2rem',
                  borderRadius: '15px',
                  boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
                  transition: 'all 0.3s ease',
                  border: '1px solid rgba(37, 99, 235, 0.1)'
                }}>
                  <div style={{
                    width: '60px',
                    height: '60px',
                    background: 'linear-gradient(135deg, #2563eb, #3b82f6)',
                    borderRadius: '15px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    marginBottom: '1.5rem',
                    fontSize: '1.5rem'
                  }}>
                    {icons[index]}
                  </div>
                  <h4 style={{
                    fontSize: '1.2rem',
                    fontWeight: '600',
                    marginBottom: '1rem',
                    color: '#1f2937'
                  }}>
                    {component.title}
                  </h4>
                  <p style={{
                    color: '#6b7280',
                    lineHeight: '1.6'
                  }}>
                    {component.description}
                  </p>
                </div>
              );
            })}
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section id="features" style={{ padding: '80px 0', backgroundColor: '#f8fafc' }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 2rem' }}>
          <h2 style={{
            fontSize: 'clamp(2rem, 4vw, 3rem)',
            fontWeight: '700',
            textAlign: 'center',
            marginBottom: '1rem',
            background: 'linear-gradient(135deg, #2563eb, #3b82f6)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            {t.features.title}
          </h2>
          <p style={{
            fontSize: '1.2rem',
            textAlign: 'center',
            color: '#6b7280',
            marginBottom: '3rem'
          }}>
            {t.features.subtitle}
          </p>

          <div style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '1rem',
            marginBottom: '4rem'
          }}>
            <button
              onClick={() => setTargetAudience('residential')}
              style={{
                padding: '1rem 2rem',
                borderRadius: '50px',
                border: 'none',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                background: targetAudience === 'residential' 
                  ? 'linear-gradient(135deg, #2563eb, #3b82f6)' 
                  : 'white',
                color: targetAudience === 'residential' ? 'white' : '#2563eb',
                boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)'
              }}
            >
              {t.features.targetSelector.residential}
            </button>
            <button
              onClick={() => setTargetAudience('commercial')}
              style={{
                padding: '1rem 2rem',
                borderRadius: '50px',
                border: 'none',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                background: targetAudience === 'commercial' 
                  ? 'linear-gradient(135deg, #2563eb, #3b82f6)' 
                  : 'white',
                color: targetAudience === 'commercial' ? 'white' : '#2563eb',
                boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)'
              }}
            >
              {t.features.targetSelector.commercial}
            </button>
          </div>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
            gap: '2rem'
          }}>
            {Object.entries(t.features[targetAudience]).map(([key, feature], index) => {
              const icons = ['ðŸ”’', 'ðŸ’°', 'ðŸ“‰', 'â˜€ï¸', 'ðŸš—', 'ðŸ '];
              return (
                <div key={key} style={{
                  background: 'white',
                  padding: '2rem',
                  borderRadius: '15px',
                  boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
                  transition: 'all 0.3s ease',
                  border: '1px solid rgba(37, 99, 235, 0.1)'
                }}>
                  <div style={{
                    width: '60px',
                    height: '60px',
                    background: 'linear-gradient(135deg, #10b981, #059669)',
                    borderRadius: '15px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    marginBottom: '1.5rem',
                    fontSize: '1.5rem'
                  }}>
                    {icons[index % icons.length]}
                  </div>
                  <h3 style={{
                    fontSize: '1.3rem',
                    fontWeight: '600',
                    marginBottom: '1rem',
                    color: '#1f2937'
                  }}>
                    {feature.title}
                  </h3>
                  <p style={{
                    color: '#6b7280',
                    lineHeight: '1.6',
                    marginBottom: '1rem'
                  }}>
                    {feature.description}
                  </p>
                  <div style={{
                    background: 'linear-gradient(135deg, rgba(16, 185, 129, 0.1), rgba(5, 150, 105, 0.1))',
                    padding: '0.75rem 1rem',
                    borderRadius: '10px',
                    border: '1px solid rgba(16, 185, 129, 0.2)'
                  }}>
                    <span style={{
                      fontSize: '0.9rem',
                      fontWeight: '600',
                      color: '#059669'
                    }}>
                      ðŸ’¡ {feature.savings}
                    </span>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </section>

      {/* Advantages Section */}
      <section id="advantages" style={{ padding: '80px 0', backgroundColor: 'white' }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 2rem' }}>
          <h2 style={{
            fontSize: 'clamp(2rem, 4vw, 3rem)',
            fontWeight: '700',
            textAlign: 'center',
            marginBottom: '1rem',
            background: 'linear-gradient(135deg, #2563eb, #3b82f6)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            {t.advantages.title}
          </h2>
          <p style={{
            fontSize: '1.2rem',
            textAlign: 'center',
            color: '#6b7280',
            marginBottom: '4rem',
            maxWidth: '600px',
            marginLeft: 'auto',
            marginRight: 'auto'
          }}>
            {t.advantages.subtitle}
          </p>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
            gap: '2rem'
          }}>
            {Object.entries(t.advantages).slice(2).map(([key, advantage], index) => {
              const icons = ['ðŸ’°', 'ðŸ¤–', 'ðŸ”—', 'ðŸŒ±', 'ðŸ‡µðŸ‡¹', 'âœ…'];
              return (
                <div key={key} style={{
                  background: 'linear-gradient(135deg, rgba(37, 99, 235, 0.05), rgba(59, 130, 246, 0.05))',
                  padding: '2rem',
                  borderRadius: '15px',
                  border: '1px solid rgba(37, 99, 235, 0.1)',
                  transition: 'all 0.3s ease'
                }}>
                  <div style={{
                    width: '60px',
                    height: '60px',
                    background: 'linear-gradient(135deg, #2563eb, #3b82f6)',
                    borderRadius: '15px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    marginBottom: '1.5rem',
                    fontSize: '1.5rem'
                  }}>
                    {icons[index]}
                  </div>
                  <h3 style={{
                    fontSize: '1.3rem',
                    fontWeight: '600',
                    marginBottom: '1rem',
                    color: '#1f2937'
                  }}>
                    {advantage.title}
                  </h3>
                  <p style={{
                    color: '#6b7280',
                    lineHeight: '1.6'
                  }}>
                    {advantage.description}
                  </p>
                </div>
              );
            })}
          </div>
        </div>
      </section>

      {/* Pricing Section */}
      <section id="pricing" style={{ padding: '80px 0', backgroundColor: '#f8fafc' }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 2rem' }}>
          <h2 style={{
            fontSize: 'clamp(2rem, 4vw, 3rem)',
            fontWeight: '700',
            textAlign: 'center',
            marginBottom: '1rem',
            background: 'linear-gradient(135deg, #2563eb, #3b82f6)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            {t.pricing.title}
          </h2>
          <p style={{
            fontSize: '1.2rem',
            textAlign: 'center',
            color: '#6b7280',
            marginBottom: '4rem'
          }}>
            {t.pricing.subtitle}
          </p>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
            gap: '2rem',
            marginBottom: '4rem'
          }}>
            {Object.entries(t.pricing.comparison).map(([key, product], index) => (
              <div key={key} style={{
                background: key === 'portev' ? 'linear-gradient(135deg, #2563eb, #3b82f6)' : 'white',
                color: key === 'portev' ? 'white' : '#1f2937',
                padding: '2rem',
                borderRadius: '15px',
                boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
                border: key === 'portev' ? 'none' : '1px solid rgba(37, 99, 235, 0.1)',
                position: 'relative',
                transform: key === 'portev' ? 'scale(1.05)' : 'scale(1)',
                transition: 'all 0.3s ease'
              }}>
                {key === 'portev' && (
                  <div style={{
                    position: 'absolute',
                    top: '-10px',
                    left: '50%',
                    transform: 'translateX(-50%)',
                    background: '#10b981',
                    color: 'white',
                    padding: '0.5rem 1rem',
                    borderRadius: '20px',
                    fontSize: '0.9rem',
                    fontWeight: '600'
                  }}>
                    {t.pricing.ourSolution}
                  </div>
                )}

                <h3 style={{
                  fontSize: '1.3rem',
                  fontWeight: '600',
                  marginBottom: '1rem',
                  textAlign: 'center'
                }}>
                  {product.title}
                </h3>

                <div style={{
                  fontSize: '1.8rem',
                  fontWeight: '700',
                  textAlign: 'center',
                  marginBottom: '1rem'
                }}>
                  {product.price}
                </div>

                <div style={{
                  fontSize: '1rem',
                  textAlign: 'center',
                  marginBottom: '2rem',
                  opacity: 0.8
                }}>
                  {product.capacity}
                </div>

                <ul style={{
                  listStyle: 'none',
                  padding: 0,
                  margin: 0
                }}>
                  {product.features.map((feature, idx) => (
                    <li key={idx} style={{
                      padding: '0.5rem 0',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '0.5rem'
                    }}>
                      <span style={{ color: key === 'portev' ? '#60a5fa' : '#10b981' }}>âœ“</span>
                      {feature}
                    </li>
                  ))}
                </ul>
              </div>
