// /src/components/AdvantagesSection.jsx

import React from 'react';
import styles from '../PortEVLanding.module.css';

// IMPORTANT: Place your product image in the /public folder
// or import it from /src/assets if you have it there.
// import productImage from '../assets/product-image.png';

const AdvantagesSection = ({ t }) => {
  const advantages = Object.values(t.advantages).slice(2);
  const icons = ['ðŸ’°', 'ðŸ¤–', 'ðŸ”—', 'ðŸŒ±', 'ðŸ‡µðŸ‡¹', 'âœ…'];

  return (
    <section id="advantages" className={`${styles.section} ${styles.advantagesSection}`}>
      <div className={styles.container}>
        <h2 className={styles.sectionTitle}>{t.advantages.title}</h2>
        <p className={styles.sectionSubtitle}>{t.advantages.subtitle}</p>

        {/* New Split Layout Container */}
        <div className={styles.splitLayout}>

          {/* Column 1: Product Image */}
          <div className={styles.splitImageContainer}>
            {/* RECOMMENDATION: Place your image in the /public folder and use the path like below.
              It's the easiest way for static assets.
            */}
            <img src="../assets/logo2.png" alt="PortEV energy storage unit" className={styles.productImage} />
          </div>

          {/* Column 2: List of Advantages */}
          <div className={styles.splitContentContainer}>
            <ul className={styles.advantageList}>
              {advantages.map((advantage, index) => (
                <li key={advantage.title} className={styles.advantageItem}>
                  <div className={styles.advantageIcon}>
                    {icons[index % icons.length]}
                  </div>
                  <div className={styles.advantageText}>
                    <h3 className={styles.advantageTitle}>{advantage.title}</h3>
                    <p className={styles.advantageDescription}>{advantage.description}</p>
                  </div>
                </li>
              ))}
            </ul>
          </div>

        </div>
      </div>
    </section>
  );
};

export default AdvantagesSection;
